CREATE TABLE "IPM"
(
    "ID" SERIAL PRIMARY KEY,
    "Product (WPN)"	VARCHAR(512),
    "Datasheet"	VARCHAR(512),
    "OPN"	VARCHAR(512),
    "Price"	FLOAT,
    "Description"	VARCHAR(512),
    "Rated Voltage Min (V)"	INT,
    "VCE(sat) @ 25 °C Typ (V)"	FLOAT,
    "IO @ 25 °C Min (A)"	FLOAT,
    "RDS(on) (Ω)"	FLOAT,
    "Family"	VARCHAR(512),
    "Topology"	VARCHAR(512),
    "Package Type"	VARCHAR(512),
    "Substrate"	VARCHAR(512),
    "Input Logic"	VARCHAR(512),
    "Shutdown Pin"	BOOLEAN,
    "Emitter Configuration"	VARCHAR(512),
    "Mounting Type"	VARCHAR(512),
    "PMAX (W)"	INT,
    "Qualification"	VARCHAR(512),
    "AEC Qualified"	BOOLEAN,
    "PPAP Capable"	BOOLEAN,
    "Halide Free"	BOOLEAN,
    "Lead Free"	BOOLEAN,
    "Status"	VARCHAR(512)
);

